import React,{useEffect,useState} from 'react'
import Modal from 'react-modal'
import './dashboard.scss';
import axios from 'axios';
function Dashboard() {
    const [userRole,setUserRole] = useState(null);
    const [modalIsOpen, setModalIsOpen] = useState(false);
    useEffect(()=>{
        const fetchUserIn = async()=>{
            try {
                const response = await axios.get("http://localhost:5000/userin", {withCredentials:true})
                setUserRole(response.data.role);
            } catch (error) {
                console.log(error);
            }
        }
        fetchUserIn();
    }) 
  return (
    <div>
      <div className='dashboardImage'>
        <div className='dashboardImageTag'>{userRole}</div>
      </div>
      {userRole ==="teacher" && (
          <>
          
            <button type ="submit" name="createcourse">Create New Course</button>
            <Modal isOpen={modalIsOpen} ariaHideApp={false}></Modal>
          
          </>
      )}
    </div>
  )
}

export default Dashboard
