import React,{useState,useEffect} from 'react'
import {useParams} from 'react-router-dom'
import axios from 'axios';
import './coursedetail.scss';
function CourseDetail() {
    const {slug} = useParams();
    const [course,setCourse] = useState({});
    const [userRole,setUserRole] = useState(null);
    useEffect(()=>{
      const fetchCourses = async()=>{
        const response = await axios.get("http://localhost:5000/courses",{withCredentials:true})
        .then(response=>{
          console.log(response.data)
          setUserRole(response.data.course);
        })
      }
      const fetchUserRole = async()=>{
        const response = await axios.get("http://localhost:5000/userin",{withCredentials:true})
        .then(response=>{
          console.log(response.data);
          setUserRole(response.data.userRole);
        })
      }
      fetchUserRole();
      fetchCourses();
    },[slug])
  return (
    <div>
      <div className='coursesImage'></div>
      <div className='courseContainer'>
        <div className='courseImage'></div>
        <div className='courseDetail'>
          <div className='courseName'>{course.name}</div>
          <div className='courseDesc'>{course.description}</div>
          {userRole === 'student' && (
            <>
            <form method='POST' action={`http://localhost:5000/courses/enroll`}></form>
            </>
          )}
        </div>
      </div>
    </div>
  )
}

export default CourseDetail
