import React,{useEffect,useState} from 'react'
import './dashboard.scss';
import axios from 'axios';
function Dashboard() {
    const [userRole,setUserRole] = useState(null);
    useEffect(()=>{
        const fetchUserIn = async()=>{
            try {
                const response = await axios.get("http://localhost:5000/userin", {withCredentials:true})
                setUserRole(response.data.role);
            } catch (error) {
                console.log(error);
            }
        }
        fetchUserIn();
    }) 
  return (
    <div>
      <div className='dashboardImage'>
        <div className='dashboardImageTag'>{userRole}</div>
      </div>
      {userRole ==="teacher" && (
          <>
          <input type = "submit" name="createcourse" className='createCourse'>CreateCourse</input>
          </>
      )}
    </div>
  )
}

export default Dashboard
